<?php	class articles extends Controller {		function __construct() {		}		function index(){            $result= $this->model->options();			$this->view('articles/index',$result);		}        function tab()        {            $number = $_POST['number'];            if ($number==0) {                $articles= $this->model->category();                $this->view( 'articles/site_design',$articles,1,1);            }            if ($number==1) {                $articles= $this->model->category();                $this->view( 'articles/optimizing_site',$articles,1,1);            }        }    }?>