<?php	class categorizeArticles extends Controller {		function __construct() {		}		function index(){            $result = $this->model;			$this->view('categorizeArticles/index',$result);		}        function tab_categorizeArticles()        {            $number = $_POST['number'];            if ($number==0) {                $articles= $this->model->category();                $this->view( 'categorizeArticles/site_design',$articles,1,1);            }            if ($number==1) {                $articles= $this->model->category();                $this->view( 'categorizeArticles/optimizing_site',$articles,1,1);            }        }    }?>